package com.example.mylibrary;

import java.util.ArrayList;

public class Util {

    private static ArrayList<book> allBooks;
    private static ArrayList<book> currentlyReadingBooks;
    private static ArrayList<book> wantToReadAllBooks;
    private static ArrayList<book> alreadyReadBooks;
    private static int id=0;

    public Util(){
        if (allBooks == null){
            allBooks=new ArrayList<>();
            initAllBooks();
        }
        if(currentlyReadingBooks ==null){
            currentlyReadingBooks = new ArrayList<>();
        }
        if(wantToReadAllBooks==null){
            wantToReadAllBooks=new ArrayList<>();
        }
        if(null==alreadyReadBooks){
            alreadyReadBooks =new ArrayList<>();
        }
    }


    public static ArrayList<book> getAllBooks() {
        return allBooks;
    }

    public static ArrayList<book> getCurrentlyReadingBooks() {
        return currentlyReadingBooks;
    }

    public static ArrayList<book> getWantToReadAllBooks() {
        return wantToReadAllBooks;
    }

    public static ArrayList<book> getAlreadyReadBooks() {
        return alreadyReadBooks;
    }
    public boolean addCurrentlyReadingBooks(book book){
        return currentlyReadingBooks.add(book);
    }
    public boolean addWantToReadBooks(book book){
        return wantToReadAllBooks.add(book);
    }
    public boolean addAlreadyToReadBook(book book){
        return alreadyReadBooks.add(book);
    }
    public boolean removeCurrentlyReadingBooks(book book){
        return currentlyReadingBooks.remove(book);
    }
    public boolean removeWantToReadBooks(book book){
        return wantToReadAllBooks.remove(book);
    }
    public boolean removeAlreadyToReadBook(book book){
        return alreadyReadBooks.remove(book);
    }

    private static void initAllBooks(){
        String name = "";
        String author = "";
        int pages = 0;
        String imageUrl="";
        String description="";

        id++;
        name="1984";
        author="George Orwell";
        pages=237;
        imageUrl="data:image/jpeg;base64,";
        description="Among the seminal texts of the 20th century, Nineteen Eighty-Four is a rare work that grows more haunting as its futuristic purgatory becomes more real.";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="Pride and Prejudice";
        author="Jane Austen, Anna Quindlen";
        pages=279;
        imageUrl="data:image/jpeg;base64,";
        description="Since its immediate success in 1813, Pride and Prejudice has remained one of the most popular novels in the English language.";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="To Kill a Mockingbird";
        author="Harper Lee";
        pages=324;
        imageUrl="data:image/jpeg;base64,";
        description="The unforgettable novel of a childhood in a sleepy Southern town and the crisis of conscience that rocked it,";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="The Great Gatsby";
        author="F. Scott Fitzgerald, Francis Scott Fitzgerald";
        pages=200;
        imageUrl="data:image/jpeg;base64,";
        description="The Great Gatsby, F. Scott Fitzgerald's third book, stands as the supreme achievement of his career. ";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="Catch-22";
        author="Joseph Heller";
        pages=453;
        imageUrl="data:image/jpeg;base64,";
        description=" Most of the events in the book occur while the fictional 256th Squadron is based on the island of Pianosa, in the Mediterranean Sea";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="The Catcher in the Rye";
        author="J.D. Salinger";
        pages=277;
        imageUrl="data:image/jpeg;base64,";
        description="Through circumstances that tend to preclude adult, secondhand description, he leaves his prep school in Pennsylvania and goes underground in New York City for three days";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="Animal Farm";
        author="George Orwell, Russell Baker ";
        pages=141;
        imageUrl="data:image/jpeg;base64,";
        description="A farm is taken over by its overworked, mistreated animals. With flaming idealism and stirring slogans, they set out to create a paradise of progress, justice, and equality.";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="Great Expectations";
        author="Charles Dickens, Marisa Sestino";
        pages=505;
        imageUrl="data:image/jpeg;base64,";
        description="In what may be Dickens's best novel, humble, orphaned Pip is apprenticed to the dirty work of the forge but dares to dream of becoming a gentleman";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="Jane Eyre";
        author="Charlotte Brontë, Michael Mason";
        pages=532;
        imageUrl="data:image/jpeg;base64,";
        description="Orphaned as a child, Jane has felt an outcast her whole young life. Her courage is tested once again when she arrives at Thornfield Hall";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

        id++;
        name="Wuthering Heights";
        author="Emily Brontë, Richard J. Dunn";
        pages=464;
        imageUrl="data:image/jpeg;base64,";
        description="This best-selling Norton Critical Edition is based on the 1847 first edition of the novel. For the Fourth Edition, the editor has collated the 1847 text with several modern editions and has corrected a number of variants, including accidentals";
        allBooks.add(new book(id,name,author,pages,imageUrl,description));

    }
}
